recomendacoes.exemploCSS:
.skip-link {
  position: absolute;
  left: -9999px;
}

.skip-link:focus {
  left: 0;
  top: 0;
  z-index: 9999;
  padding: 10px;
  background: #000;
  color: #FFF;
}

recomendacoes.exemploJS:
// BOM - Acess√≠vel via teclado
button.addEventListener('click', handleClick);
button.addEventListener('keydown', (e) => {
  if (e.key === 'Enter' || e.key === ' ') {
    e.preventDefault();
    handleClick();
  }
});

// RUIM - S√≥ mouse
div.addEventListener('click', handleClick);

// Avisar 2 minutos antes do timeout
setTimeout(() => {
  if (confirm('Sua sess√£o expira em 2 minutos. Continuar?')) {
    estenderSessao();
  }
}, sessionTime - 120000);

