implementacao.css:
/* Não forçar orientação */
@media (orientation: portrait) {
  /* Adaptações para retrato */
}

@media (orientation: landscape) {
  /* Adaptações para paisagem */
}

/* BOM - Contraste 7:1 (AAA) */
body {
  color: #000000; /* Preto */
  background-color: #FFFFFF; /* Branco */
}

/* RUIM - Contraste 2.9:1 */
.texto-cinza {
  color: #999999;
  background-color: #FFFFFF;
}

/* BOM - Unidades relativas */
body {
  font-size: 1rem; /* 16px base */
}

h1 {
  font-size: 2.5rem; /* 40px base, escalável */
}

/* RUIM - Pixels fixos */
.texto-fixo {
  font-size: 14px; /* Não escala bem */
}

/* AAA - Contraste 21:1 */
.tema-aaa {
  color: #000000;
  background-color: #FFFFFF;
}

/* AAA - Contraste 7.5:1 */
.texto-secundario {
  color: #555555;
  background-color: #FFFFFF;
}

/* AAA - Apresentação visual otimizada */
.texto-conteudo {
  max-width: 70ch; /* ~80 caracteres */
  line-height: 1.7; /* Espaçamento entre linhas */
  text-align: left; /* Não justificado */
  letter-spacing: 0.05em; /* Espaçamento entre letras */
}

.texto-conteudo p + p {
  margin-top: 1.5em; /* Espaçamento entre parágrafos */
}

/* Design responsivo */
@media (max-width: 400px) {
  .container {
    display: flex;
    flex-direction: column;
    max-width: 100%;
  }
}

/* Contraste 3:1 em bordas */
button {
  background: #FFF;
  border: 2px solid #767676; /* Contraste 3.3:1 com branco */
}

/* Focus visível com contraste */
button:focus {
  outline: 3px solid #005A9C; /* Contraste 7.1:1 */
  outline-offset: 2px;
}

/* Layout resiliente a espaçamento */
body {
  line-height: 1.5;
  letter-spacing: 0.12em;
  word-spacing: 0.16em;
}

p {
  margin-bottom: 2em; /* 2x font-size */
}
